import React from 'react';
import Square from './Square'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.state = {
            squares: []
        }
    }

    componentDidMount() {
        this.prepareSquares();
    }

    prepareSquares() {
        let squareColor = 1, squares = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [];
            for (let index = 0; index < this.dimensions.width; index++) {
                row.push(squareColor); if (squareColor === 1) squareColor = 0; else squareColor = 1;
            }
            squares.push(row); 
        }
        this.setState({squares: squares}, () => console.log(this.state.squares));
    }

    render() {
        return (
            <div id="Board">
                {this.state.squares.map((square, id) => <Square key={id} color={square === 1 ? 'white' : 'black'} />)}
            </div>
        );
    }
}

export default Board;
