import React from 'react';
import Row from './Row'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.vCoordinates = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
        this.hCoordinates = ['8', '7', '6', '5', '4', '3', '2', '1'];

        this.state = {
            rows: [],
            initialSet: 'whitesOnTop'
        }

        this.updateBoard = this.updateBoard.bind(this);
    }

    componentDidMount() {
        this.updateBoard();
    }

    updateBoard() {
        let hCoordinate = -1, color = 1, rows = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [], vCoordinate = -1; hCoordinate++;
            for (let index = 0; index < this.dimensions.width; index++) {
                vCoordinate++; 
                const coordinate = `${this.props.board.vCoordinates[vCoordinate]}${this.props.board.hCoordinates[hCoordinate]}`
                row.push({coordinates: coordinate, color: color, piece: this.props.situation[this.state.initialSet][coordinate]}); 
                if (index !== this.dimensions.width - 1) 
                    if (color === 1) color = 0; 
                    else color = 1;
            }
            rows.push(row); 
        }
        this.setState({rows: rows});
    }

    render() {
        let files = [], ranks = [];
        if (this.state.rows[0]) {
            const row = this.state.rows[0];
            row.forEach(square => files.push(square.coordinates.split('')[0]));
        }
        for (let i = 1; i < this.state.rows.length + 1; i++)
            ranks.push(i);
        return (
            <div id="Board">
                <div className="Content">
                    {this.state.rows.map((row, id) => <Row key={id} row={row} />)}
                </div>
                <div id="FileStrip" className="strip">{files.map((file, id) => <div key={id} className="File">{file}</div>)}</div>
                <div id="RankStrip" className="strip">{ranks.map((rank, id) => <div key={id} className="Rank">{rank}</div>)}</div>
            </div>
        );
    }
}

export default Board;
