import React from 'react';
import Row from './Row'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.state = {

        }

        this.updateBoard = this.setBoard.bind(this);
    }

    componentDidMount() {
        this.setBoard();
    }

    setBoard() {
        let hCoordinate = -1, color = 1, situation = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [], vCoordinate = -1; hCoordinate++;
            for (let index = 0; index < this.dimensions.width; index++) {
                vCoordinate++; 
                const coordinate = `${this.props.board.vCoordinates[vCoordinate]}${this.props.board.hCoordinates[hCoordinate]}`
                row.push({coordinates: coordinate, color: color, piece: this.props.initialSet[this.props.initialSetType][coordinate]}); 
                if (index !== this.dimensions.width - 1) 
                    if (color === 1) color = 0; 
                    else color = 1;
            }
            situation.push(row); 
        }
        this.props.updateSituation(situation);
    }

    render() {
        let files = [], ranks = [];
        if (this.props.situation[0]) {
            const row = this.props.situation[0];
            row.forEach(square => files.push(square.coordinates.split('')[0]));
        }
        for (let i = 1; i < this.props.situation.length + 1; i++)
            ranks.push(i);
        return (
            <div id="Board">
                <div className="Content">
                    {this.props.situation.map((row, id) => <Row key={id} row={row} />)}
                </div>
                <div id="FileStrip" className="strip">{files.map((file, id) => <div key={id} className="File">{file}</div>)}</div>
                <div id="RankStrip" className="strip">{ranks.map((rank, id) => <div key={id} className="Rank">{rank}</div>)}</div>
            </div>
        );
    }
}

export default Board;
