import React from 'react';
import Input from './Input'
import Buttons from './Buttons'
import './Panel.css';
import MovesHistory from './MovesHistory';

class Panel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            moves: [],
            notation: ''
        }

        this.updateNotation = this.updateNotation.bind(this);
        this.addNotation = this.addNotation.bind(this);
        this.validateNotation = this.validateNotation.bind(this);
    }

    updateNotation(event) {
        this.setState({notation: event.target.value})
    }

    addNotation(event) {
        event.preventDefault();
        if (this.state.notation !== '')
