import React from 'react';
import Row from './Row'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.vCoordinates = new Set(['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']);
        this.hCoordinates = new Set(['1', '2', '3', '4', '5', '6', '7', '8']);
        this.initialSet = new Map([
            ['whitesOnTop', new Map(['A1', 'white-rook'], ['B1', 'white-knight'], ['C1', 'white-bishop'], ['D1', 'white-king'], ['E1', 'white-queen'], ['F1', 'white-bishop'], ['G1', 'white-knight'], ['H1', 'white-rook'], 
                                    ['A2', 'white-pawn'], ['B2', 'white-pawn'], ['C2', 'white-pawn'], ['D2', 'white-pawn'], ['E2', 'white-pawn'], ['F2', 'white-pawn'], ['G2', 'white-pawn'], ['H2', 'white-pawn'],
                                    ['A7', 'black-pawn'], ['B7', 'black-pawn'], ['C7', 'black-pawn'], ['D7', 'black-pawn'], ['E7', 'black-pawn'], ['F7', 'black-pawn'], ['G7', 'black-pawn'], ['H7', 'black-pawn'],
                                    ['A8', 'black-rook'], ['B8', 'black-knight'], ['C8', 'black-bishop'], ['D8', 'black-king'], ['E8', 'black-queen'], ['F8', 'black-bishop'], ['G8', 'black-knight'], ['H8', 'black-rook'])], 

            ['blacksOnTop', new Map(['A1', 'black-rook'], ['B1', 'black-knight'], ['C1', 'black-bishop'], ['D1', 'black-king'], ['E1', 'black-queen'], ['F1', 'black-bishop'], ['G1', 'black-knight'], ['H1', 'black-rook'], 
                                    ['A2', 'black-pawn'], ['B2', 'black-pawn'], ['C2', 'black-pawn'], ['D2', 'black-pawn'], ['E2', 'black-pawn'], ['F2', 'black-pawn'], ['G2', 'black-pawn'], ['H2', 'black-pawn'],
                                    ['A7', 'white-pawn'], ['B7', 'white-pawn'], ['C7', 'white-pawn'], ['D7', 'white-pawn'], ['E7', 'white-pawn'], ['F7', 'white-pawn'], ['G7', 'white-pawn'], ['H7', 'white-pawn'],
                                    ['A8', 'white-rook'], ['B8', 'white-knight'], ['C8', 'white-bishop'], ['D8', 'white-king'], ['E8', 'white-queen'], ['F8', 'white-bishop'], ['G8', 'white-knight'], ['H8', 'white-rook'])]
        ]);
        this.state = {
            rows: [],
            initialSet: 'whitesOnTop'
        }

        this.updateBoard = this.updateBoard.bind(this);
    }

    componentDidMount() {
        this.updateBoard();
    }

    updateBoard() {
        let hCoordinate = -1, color = 1, rows = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [], vCoordinate = -1; hCoordinate++;
            for (let index = 0; index < this.dimensions.width; index++) {
                vCoordinate++; const coordinate = 
                row.push({coordinates: `${this.vCoordinates[vCoordinate]}${this.hCoordinates[hCoordinate]}`, 
                          color: color, piece: this.initialSet.get(this.state.initialSet).get(`${this.vCoordinates[vCoordinate]}${this.hCoordinates[hCoordinate]}`)}); 
                if (index !== this.dimensions.width - 1) 
                    if (color === 1) color = 0; 
                    else color = 1;
            }
            rows.push(row); 
        }
        this.setState({rows: rows});
    }

    render() {
        return (
            <div id="Board">
                <div className="Content">
                    {this.state.rows.map((row, id) => <Row key={id} row={row} />)}
                </div>
            </div>
        );
    }
}

export default Board;
