import React from 'react';
import './SessionsPanel.css';
import Session from './Session'
import PouchDB from 'pouchdb-browser';

class SessionsPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sessions: [{id: 1, title: 'Session'}, {id: 2, title: 'Session'}, {id: 3, title: 'Session'}]
        }
    }

    componentDidMount() {
        const db = PouchDB('chess-moves-parser');
        db.info().then(function (info) {
            console.log(info);
          });
    }

    render() {
        return (
            <section id="SessionsPanel" className={this.props.visible ? 'visible' : null}>
                <section id="SessionsPanelButtons">
                    <button className="button" onClick={this.props.hideSessionsPanel}>Back</button>
                </section>
                <section id="SessionsList">
                    {this.state.sessions.map(session => <Session key={session.id} title={session.title} />)}
                </section>
                <section id="Buttons">
                    <button onClick={this.props.showFileChooserForm}>Load from file</button>
                </section>
            </section>
        );
    }
}

export default SessionsPanel;
