import React from 'react';
import Input from './Input'
import Buttons from './Buttons'
import SessionsPanel from './SessionsPanel'
import './Panel.css';
import MovesHistory from './MovesHistory';

class Panel extends React.Component {
    constructor(props) {
        super(props);
        this.notationMinLength = 5;
        this.notationMaxLength = 5;
        this.state = {
            moves: [],
            notationsList: '',
            error: '',
            sessionsPanelVisible: false
        }
        this.updateNotationsList = this.updateNotationsList.bind(this);
        this.parseNotationsList = this.parseNotationsList.bind(this);
        this.openSessionsPanel = this.openSessionsPanel.bind(this);
        this.hideSessionsPanel = this.hideSessionsPanel.bind(this);
    }

    updateNotationsList(event) {
        this.setState({error: ''});
        let notationsList = event.target.value.trim();
        if (notationsList.length > 0) {
            const split = notationsList.split('');
            split[0] = split[0].toUpperCase();
            if (notationsList.length === 2)
                split[1] = split[1].toLowerCase();
            if (notationsList.length === 4)
                split[3] = split[3].toLowerCase();
            this.setState({notationsList: split.join('')});
        } else {
            this.setState({notationsList: notationsList});
        }
    }

    validateNotation(notation) {
        const minLength = this.notationMinLength, maxLength = this.notationMaxLength;
        function checkLength() {
            return notation.length >= minLength && notation.length <= maxLength;
        }
        function checkComposition() {
            return notation.match(/^[A-Z-a-z]{1}[A-Z-a-z]{1}\d{1}[A-Za-z]{1}\d{1}/i);
        }
        return true;
    }

    getLatestMoveId() {
        if (this.state.moves.length === 0)
            return -1;
        else
            return this.state.moves[0].id;
    }

    disectNotation(notation) {
        const split = notation.split('');
        return {piece: split[0], from: split[1] + split[2], to: split[3] + split[4]};
    }

    parseNotationsList(event) {
        event.preventDefault();
        if (this.state.notationsList.trim().length > 0) {
            // if (this.validateNotation(this.state.notationsList)) {
            if (this.state.notationsList.trim().length !== 0) {
                this.setState(state => {
                    const notations = this.state.notationsList.split(/\n/);
                    let allNotationsValid = true;
                    for (let notation of notations)
                        if (this.validateNotation(notation.trim())) {
                            allNotationsValid = false;
                            this.setState({error: 'Invalid notation'});
                        }
                    if (allNotationsValid) {
                        for (let notation of notations) {
                            const id = this.getLatestMoveId() + 1;
                            state.moves.unshift({id: id, notation: notation, side: id === 1 ? 'Whites' : 'Blacks'});
                        }
                    }
                    return {moves: state.moves}
                }, () => {this.setState({notation: ''}); this.props.makeMove(this.disectNotation(this.state.notationsList))});
            }
        }
    }

    openSessionsPanel() {
        this.setState({sessionsPanelVisible: true});
    }

    hideSessionsPanel() {
        this.setState({sessionsPanelVisible: false});
    }

    render() {
        return (
            <aside id="Panel">
                <Input notationsList={this.state.notationsList} updateNotationsList={this.updateNotationsList} parseNotationsList={this.parseNotationsList} />
                {this.state.error && <div id="Error">{this.state.error}</div>}
                <h3>{this.state.moves.length !== 0 && 'Session History'}</h3>
                <hr />
                <MovesHistory moves={this.state.moves} side={this.props.side} />
                <Buttons openSessionsPanel={this.openSessionsPanel} />
                <SessionsPanel visible={this.state.sessionsPanelVisible} hideSessionsPanel={this.hideSessionsPanel} />
            </aside>
        );
    }
}

export default Panel;
