import PouchDB from 'pouchdb-browser';

class Database {
    constructor() {
        this.remoteDBURL = 'http://localhost:5984/';
        this.dbName = 'chess-moves-parser';
    }

    init() {
        const localDB = PouchDB(this.dbName);
        const remoteDB = PouchDB(`${this.remoteDBURL}${this.dbName}`);
        // db.info().then(function (info) {
        //     console.log(info);
        //   });
        localDB.replicate.to(remoteDB).on('complete', function () {
            this.onSync();
        }).on('error', function (err) {
            this.onSyncError();
        });
    }

    onSync() {
        console.log('Local DB synced succssfully to remote one.');
    }

    onSyncError() {
        console.log('Error ocurred during the sync of local DB to remote one.');
    }
}

export default Database;
