import React from 'react';
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.squares = [];
    }

    componentDidMount() {
        this.prepareSquares();
    }

    prepareSquares() {
        let squareColor = 1;
        for (let index = 0; index < this.dimensions.width * this.dimensions.height; index++) {
            this.squares.push(squareColor); if (squareColor === 1) squareColor = 0; else squareColor = 1;
        }
    }

    render() {
        const fields = this.squares.map((square, id) => 
        <div key={id} className={`square ${square === 1 ? 'white' : 'black'}`}>{id}</div>
        );
        return (
            <div id="Board">
                {fields}
            </div>
        );
    }
}

export default Board;
