import React from 'react';
import Board from './Board';
import './App.css';
import Panel from './Panel';
import TurnIndicator from './TurnIndicator';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      turn: 'whites'
    }
  }

  render() {
    return (
      <div id="App">
        <Board />
        <TurnIndicator side={this.state.turn} />
        <Panel />
      </div>
    );
  }
}

export default App;
