import React from 'react';
import Input from './Input'
import Buttons from './Buttons'
import SessionsPanel from './SessionsPanel'
import './Panel.css';
import MovesHistory from './MovesHistory';

class Panel extends React.Component {
    constructor(props) {
        super(props);
        this.notationMinLength = 5;
        this.notationMaxLength = 5;
        this.state = {
            moves: [],
            notation: '',
            error: ''
        }
        this.updateNotation = this.updateNotation.bind(this);
        this.addNotation = this.addNotation.bind(this);
        this.validateNotation = this.validateNotation.bind(this);
    }

    updateNotation(event) {
        this.setState({error: ''});
        let notation = event.target.value.trim();
        if (notation.length > 0) {
            const split = notation.split('');
            split[0] = split[0].toUpperCase();
            if (notation.length === 2)
                split[1] = split[1].toLowerCase();
            if (notation.length === 4)
                split[3] = split[3].toLowerCase();
            this.setState({notation: split.join('')});
        } else {
            this.setState({notation: notation});
        }
    }

    validateNotation(notation) {
        const minLength = this.notationMinLength, maxLength = this.notationMaxLength;
        function checkLength() {
            return notation.length >= minLength && notation.length <= maxLength;
        }
        function checkComposition() {
            return notation.match(/^[A-Z-a-z]{1}[A-Z-a-z]{1}\d{1}[A-Za-z]{1}\d{1}/i);
        }
        function checkLegality() {
            return true;
        }
        return checkLength() && checkComposition() && checkLegality();
    }

    getLatestMoveId() {
        if (this.state.moves.length === 0)
            return -1;
        else
            return this.state.moves[0].id;
    }

    addNotation(event) {
        event.preventDefault();
        if (this.state.notation.trim().length > 0) {
            if (this.validateNotation(this.state.notation)) {
                this.setState(state => {
                    state.moves.unshift({id: this.getLatestMoveId() + 1, notation: this.state.notation, side: this.props.side}); 
                    return {moves: state.moves}
                }, () => {this.setState({notation: ''}); this.props.makeMove(this.state.notation)});
            } else {
                this.setState({error: 'Invalid notation'});
            }
        }
    }

    render() {
        return (
            <aside id="Panel">
                <Input value={this.state.notation} updateNotation={this.updateNotation} addNotation={this.addNotation} />
                {this.state.error && <div id="Error">{this.state.error}</div>}
                <h3>{this.state.moves.length !== 0 && 'Session History'}</h3>
                <hr />
                <MovesHistory moves={this.state.moves} side={this.props.side} />
                <Buttons />
                <SessionsPanel />
            </aside>
        );
    }
}

export default Panel;
