import React from 'react';
import Row from './Row'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.vCoordinates = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
        this.hCoordinates = ['1', '2', '3', '4', '5', '6', '7', '8'];
        this.state = {
            squares: []
        }

        this.updateBoard = this.updateBoard.bind(this);
    }

    componentDidMount() {
        this.updateBoard();
    }

    updateBoard() {
        let hCoordinate = -1, color = 1, squares = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [], vCoordinate = -1; hCoordinate++;
            for (let index = 0; index < this.dimensions.width; index++) {
                vCoordinate++;
                row.push({coordinates: `${this.vCoordinates[vCoordinate]}${this.hCoordinates[hCoordinate]}`, color: color, piece: null}); 
                if (index !== this.dimensions.width - 1) 
                    if (color === 1) color = 0; 
                    else color = 1;
            }
            squares.push(row); 
        }
        this.setState({squares: squares});
    }

    render() {
        return (
            <div id="Board">
                <div className="Content">
                    {this.state.squares.map((row, id) => <Row key={id} squares={row} />)}
                </div>
            </div>
        );
    }
}

export default Board;
