import React from 'react';
import Row from './Row'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.state = {
            squares: []
        }

        this.prepareBoard = this.prepareBoard.bind(this);
    }

    componentDidMount() {
        this.prepareBoard();
    }

    prepareBoard() {
        let squareColor = 1, squares = [];
        for (let index = 0; index < this.dimensions.height; index++) {
            let row = [];
            for (let index = 0; index < this.dimensions.width; index++) {
                row.push(squareColor); if (index !== this.dimensions.width - 1) if (squareColor === 1) squareColor = 0; else squareColor = 1;
            }
            squares.push(row); 
        }
        this.setState({squares: squares});
    }

    render() {
        return (
            <div id="Board">
                <div className="Content">
                    {this.state.squares.map((row, id) => <Row key={id} squares={row} />)}
                </div>
            </div>
        );
    }
}

export default Board;
