import React from 'react';
import './Input.css';


class Input extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            placeholder: "Notation...",
        }

        this.updateNotation= this.updateNotation.bind(this);
    }

    render() {
        return (
            <form id="Input"> 
                <input type="text" placeholder={this.state.placeholder} value={this.props.notation} onChange={this.props.updateNotation} />
                <input type="submit" value="Add" />
            </form>
        );
    }
}

export default Input;
