import React from 'react';
import Field from './Field'
import './Board.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.dimensions = { width: 8, height: 8 }
        this.state = {
            squares: []
        }
    }

    componentDidMount() {
        this.prepareSquares();
    }

    prepareSquares() {
        let squareColor = 1, squares = [];
        for (let index = 0; index < this.dimensions.width * this.dimensions.height; index++) {
            squares.push(squareColor); if (squareColor === 1) squareColor = 0; else squareColor = 1;
        }
        this.setState({squares: squares});
    }

    render() {
        return (
            <div id="Board">
                {this.state.squares.map((square, id) => <Field key={id} color={square === 1 ? 'white' : 'black'} />)}
            </div>
        );
    }
}

export default Board;
