import PouchDB from 'pouchdb-browser';

class Database {
    constructor() {
        this.remoteDBURL = 'http://localhost:5984/';
        this.dbName = 'chess-moves-parser';
    }

    init() {
        const localDB = PouchDB(this.dbName);
        const remoteDB = PouchDB(`${this.remoteDBURL}${this.dbName}`);
        // db.info().then(function (info) {
        //     console.log(info);
        //   });
        localDB.sync(remoteDB, {live: true}).on('change', function(change) {
            this.onSyncChange(change);
        }).on('paused', function (info) {
            this.onSyncPause(info);
        }).on('active', function (info) {
            this.onSyncActive(info);
        }).on('complete', function () {
            this.onSyncSuccess();
        }).on('error', function (err) {
            this.onSyncError(err);
        });
    }

    onSyncSuccess() {
        console.log('Local and remote DB\'s synced succssfully.');
    }

    onSyncChange(info) {
        console.log('There was a change in DB\'s data. Something was inserted, removed or updated.');
    }

    onSyncPause(info) {
        console.log('The sync between local and remote DB\'s was paused, possibly due to a lost of connection.');
    }

    onSyncActive(info) {
        console.log('The sync between local and remote DB\'s was has resumed and is now active.');
    }

    onSyncError(err) {
        console.log(`Error ocurred during the sync of local DB to remote one. ${err}`);
    }
}

export default Database;
