import PouchDB from 'pouchdb-browser';

class Database {
    constructor(dbName = 'chess-moves-parser') {
        this.dbName = dbName;
        this.init();
    }

    init() {
        const localDB = PouchDB(this.dbName, {auto_compaction: true});
        localDB.changes({
            live: true,
            since: 'now'
        }).on('change', function (change) {
            this.onDBChange(change);
        }).on('error', function (err) {
            this.onDBError(err);
        });
    }

    onDBChange(change) {
        console.log(`DB_EVENT::There was a change in the local database. Something was inserted, removed or updated.`);
    }

    onDBError(err) {
        console.log('DB_EVENT::There was an error during a database operation.');
    }

    onSyncComplete(info) {
        console.log(`DB_SYNC::A local and remote databases' data sync process has stopped. It was either either cancelled manually or finished successfully.`);
    }

    onSyncChange(info) {
        console.log(`DB_SYNC::A data sync started between local and remote databases.`);
    }

    // onSyncPause(info) {
    //     console.log(`The sync between local and remote databases was paused, either manually due to a lost of connection.`);
    // }

    onSyncActive(info) {
        console.log(`DB_SYNC::A data sync between local and remote databases was has resumed.`);
    }

    onSyncError(err) {
        console.log(`DB_SYNC::Error ocurred during the sync of local and remote databases.`);
    }
}

export default Database;
